import React from 'react';
import './App.css';
import { render } from '@testing-library/react';
import UserLogin from './UserLogin';

class Register extends React.Component{

constructor(){
  super()
  this.state={
    name: "",
    email : "",
    pswd : "",
    cpswd : ""
  }
}

updateName=(x)=>{
  this.setState({
    name : x.target.value 
  })
}
  updateEmail=(x)=>{
    this.setState({
      email : x.target.value
    })
  }
  updatePswd=(x)=>{
    this.setState({
      pswd : x.target.value
    })
  }
  updateCpswd=(x)=>{
    this.setState({
      cpswd : x.target.value
    })
  }

check =(e)=>{
  e.preventDefault()

  const regex=/^[A-Z]?[a-z]+$/;
  const result = regex.test(e.target.value)

if(this.state.name != result){
  alert("Name format is invalid") 
}


 else if(this.state.name.length <= 5){
    alert("Name should be atleast 6 character")
  }
  else if (this.state.pswd != this.state.cpswd){
    alert("Password dosen't match")
  }
  else {
    alert("Register Successfully")
    this.dataStorage()
  }
}

dataStorage=()=>{

  localStorage.setItem("name",this.state.name)
  localStorage.setItem("email",this.state.email)
  localStorage.setItem("password",this.state.pswd)
}
  render(){
  return (
    <div className="App">

        <form onSubmit={this.check}>
      <label>Name : </label>
      <input type='text' value={this.state.name} onChange={this.updateName} required></input>
      <br/><br/>
      <label>Email : </label>
      <input type='emial' value={this.state.email }onChange={this.updateEmail} required></input>
      <br/><br/>
      <label>Password : </label>
      <input type='password'value={this.state.pswd} onChange={this.updatePswd} required></input>
      <br/><br/>
      <label>Confrom Password : </label>
      <input type='password' value={this.state.cpswd} onChange={this.updateCpswd} required></input>
      <br/><br/>

      <input type='submit' value='Regiser'></input>
    </form>
    <br/>
    <br/>

<UserLogin />

    </div>
  );
  }
}

export default Register;
