import React, { useEffect, useState } from "react";
import './Table.css';
import { Link, useNavigate } from "react-router-dom";


function StudentList(){

const[data,setData]=useState("")

const navigate =useNavigate();


useEffect(()=>{
fetch("http://localhost:3006/StudenInfo")
.then(res=>res.json())
.then(resp=>setData(resp))
.catch(err=>err)
},[])

const deleteData=(id)=>{
    if(window.confirm("Are You Sure ?")){
    fetch("http://localhost:3006/StudenInfo/"+id,{
        method:"DELETE"
    })
    .then(()=>{
       alert("Deleted Successfully")
        window.location.reload()
    })

    .catch((err)=>{
        alert("error"+err)
        
    })
}
}

const EditStudentIfo=(id)=>{
navigate('./editInfo/'+id)
}

return(
    <div className="container">
        <div className="card">
           
        <h1>Student Info</h1>
        <Link to={"/form"} ><button className="btn">Add New</button></Link>
        <table>
            <thead>
                <tr>
                    <th>Id</th>
                    <th>Roll-No</th>
                    <th>Name</th>
                    <th>City</th>
                    <th>Dept</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
            {data && data.map((item)=>(
                <tr>
                   
                    <td>{item.id}</td>
                    <td>{item.rollNo}</td>
                    <td>{item.name}</td>
                    <td>{item.city}</td>
                    <td>{item.dept}</td>
                    <td><button className="del" onClick={()=>{deleteData(item.id)}}>Delete</button>&nbsp; <br/><button className="edi" onClick={()=>{EditStudentIfo(item.id)}}>Edit</button></td>
                    
                </tr>
                ))}
            </tbody>
        </table>
    </div>
    </div>
)
}
export default StudentList;
