import React, { useState } from "react";

function NumberInc(){

    const[state,setState]=useState(0)

    const inc=()=>{
        setState(state +1)
        
    }

    const dec=()=>{
        setState(state -1)
        if(state <0){
            setState(0)
        }
    }
   

    return(

        <div>
       <h6>The count is <b>{state}</b></h6>
       <button onClick={inc}>Increment</button>
       <button onClick={dec}>Decremet</button>

        </div>
    )
}

export default NumberInc;
